import { Data, GraphQLConnector, RenderedPage, RenderTemplate, Stats } from '../types';
import { GraphQLSchema } from 'graphql';
import { Config, FileLoader, PaginateConfig, RouteConfig, RoutesMap } from '../config';
export interface GenerateResult {
    pages: RenderedPage[];
    stats: Stats;
}
export interface GenerateContext {
    config: Config;
    pathPrefix: string;
    clientSchema: GraphQLSchema;
    srcPath: string;
    stats: Stats;
    renderTemplate: RenderTemplate;
    connector: GraphQLConnector;
    fileLoader: FileLoader;
}
export interface RenderContext extends GenerateContext {
    globalContext: Data;
}
export interface RouteContext {
    pathPrefix: string;
    totalPages: number;
    renderTemplate: RenderTemplate;
    route: RouteConfig;
    context: Data;
}
export interface PathItem {
    item: Data;
    path: string;
}
export interface PathItemContext {
    previous?: PathItem;
    current: PathItem;
    next?: PathItem;
}
export interface ItemBuffer {
    push(item: Data, path: string): void;
    done(): Promise<RenderedPage[]>;
}
export interface ListPageBuffer {
    push(item: Data): void;
    done(): Promise<RenderedPage[]>;
}
export interface ResolveRoutesContextParams {
    routes: RoutesMap;
    srcPath: string;
    connector: GraphQLConnector;
    stats: Stats;
}
export interface GetPaginatedData {
    (generateContext: GenerateContext, paginate: PaginateConfig, context: Data): Promise<{
        iterable: Iterable<Data>;
        total: number;
    } | null>;
}
